import { useState } from "react";

function App() {
  const [name, setName] = useState("");
  const [datetime, setDatetime] = useState("");
  const [description, setDescription] = useState("");

  const data = { name, description, datetime };

  const addNewTransaction = (e) => {
    e.preventDefault();
    const URL = process.env.REACT_APP_API_URL + "/transaction";
    fetch(URL, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: data,
    });
  };
  return (
    <main>
      <h1>
        $400<span>.00</span>
      </h1>
      <form onSubmit={addNewTransaction}>
        <div className="basic">
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            type="text"
            placeholder={"+200 new Macbook"}
          />
          <input
            value={datetime}
            onChange={(e) => setDatetime(e.target.value)}
            type="datetime-local"
          />
        </div>
        <div className="description">
          <input
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            type="text"
            placeholder={"description"}
          />
        </div>

        <button type="submit">Add new transaction</button>
      </form>

      <div className="transactions">
        <div className="transaction">
          <div className="left">
            <div className="name">New Macbook Pro M1</div>
            <div className="description">Some random description</div>
          </div>
          <div className="right">
            <div className="price red">-$1200</div>
            <div className="datetime">2022-12-10 15:35</div>
          </div>
        </div>
        <div className="transaction">
          <div className="left">
            <div className="name">New Macbook Air M1</div>
            <div className="description">Some random description</div>
          </div>
          <div className="right">
            <div className="price green">+$900</div>
            <div className="datetime">2022-12-10 15:35</div>
          </div>
        </div>
        <div className="transaction">
          <div className="left">
            <div className="name">New Macbook Pro M2</div>
            <div className="description">Some random description</div>
          </div>
          <div className="right">
            <div className="price green">+$1500</div>
            <div className="datetime">2022-12-10 15:35</div>
          </div>
        </div>
      </div>
    </main>
  );
}

export default App;
